mapper.select:
SELECT e.id, c.user_id, e.user_name, e.role_name, e.mobile, e.region_name, -- 本月统计 COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{monthStart} AND #{monthEnd} THEN c.finish_count ELSE 0 END), 0) AS finishCountMonth, COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{monthStart} AND #{monthEnd} THEN c.progress_count ELSE 0 END), 0) AS progressCountMonth, COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{monthStart} AND #{monthEnd} THEN c.cancel_count ELSE 0 END), 0) AS cancelCountMonth,
-- 本周统计 COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{weekStart} AND #{weekEnd} THEN c.finish_count ELSE 0 END), 0) AS finishCountWeek, COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{weekStart} AND #{weekEnd} THEN c.progress_count ELSE 0 END), 0) AS progressCountWeek, COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{weekStart} AND #{weekEnd} THEN c.cancel_count ELSE 0 END), 0) AS cancelCountWeek,
-- 本年统计 COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{yearStart} AND #{yearEnd} THEN c.finish_count ELSE 0 END), 0) AS finishCountYear, COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{yearStart} AND #{yearEnd} THEN c.progress_count ELSE 0 END), 0) AS progressCountYear, COALESCE(SUM(CASE WHEN c.collect_date BETWEEN #{yearStart} AND #{yearEnd} THEN c.cancel_count ELSE 0 END), 0) AS cancelCountYear
FROM tb_emp e LEFT JOIN tb_task_collect c ON e.id = c.user_id AND c.collect_date BETWEEN #{yearStart} AND #{yearEnd}
WHERE e.role_code != '1001' -- 永久排除管理员
and e.status = #{status} -- 只查询启用员工
and e.role_code = #{roleCode}
and e.user_name like concat('%', #{userName}, '%')
and e.id = #{userId}
GROUP BY e.id, e.user_name, e.role_name, e.mobile ORDER BY finishCountMonth desc

mapper.update:
update tb_emp
user_name = #{userName}, region_id = #{regionId}, region_name = #{regionName}, role_id = #{roleId}, role_code = #{roleCode}, role_name = #{roleName}, mobile = #{mobile}, image = #{image}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}

update tb_emp
set region_name = #{regionName}
where region_id = #{regionId};
